#include "xpluginflow/utils/gtest_helper.h"
std::unordered_map<std::string, std::string> MYTESTPARAM;
std::unordered_map<std::string, std::string> FilterOwnTestParam(int argc, char **argv) {
  std::unordered_map<std::string, std::string> param_map{};
  for (int i = 0; i < argc; ++i) {
    std::string arg_str = argv[i];
    if (arg_str.find("--test_") != std::string::npos) {
      auto pos = arg_str.find_first_of('=');
      std::string param_name(arg_str.begin() + 7, arg_str.begin() + pos);
      param_map[param_name] = std::string(arg_str.begin() + pos + 1, arg_str.end());
    }
  }
  return param_map;
}

